const {LINT_CATEGORY, AIRBNB_CATEGORY} = require('../../lib/generator/constants');

const title = '화살표 함수 블록에 중괄호를 사용한다.';

const rule = 'arrow-body-style';

const desc = '간결한 함수 바디(concise body)를 사용하는 경우에는 생략이 가능하다.';

const options = ['error', 'as-needed'];

const cause = null;

const lintCategory = LINT_CATEGORY.STYLISTIC_ISSUES;

const airbnbCategory = AIRBNB_CATEGORY.ARROW_FUNCTIONS;

/* eslint-disable no-unused-vars, no-undef, no-unused-expressions, no-redeclare */
/* eslint-disable arrow-body-style */
const bad = () => {
  const fn1 = () => {
    return 0;
  };
  const fn2 = () => {
    return {
      bar: {
        foo: 1,
        bar: 2,
      },
    };
  };
};
/* eslint-enable arrow-body-style */

const good = () => {
  const fn1 = () => 0;
  const fn2 = () => ({
    bar: {
      foo: 1,
      bar: 2,
    },
  });
  const fn3 = (retv, name) => {
    retv[name] = true;
    return retv;
  };
  const fn4 = () => { bar(); };
  const fn5 = () => {};
  const fn6 = () => { /* do nothing */ };
  const fn7 = () => {
    // do nothing.
  };
  const fn8 = () => ({bar: 0});
};


const best = null;
/* eslint-enable no-unused-vars, no-undef, no-unused-expressions, no-redeclare */

module.exports = {
  title,
  rule,
  options,
  desc,
  cause,
  lintCategory,
  airbnbCategory,
  bad,
  good,
  best,
};
