const {LINT_CATEGORY, AIRBNB_CATEGORY} = require('../../lib/generator/constants');

const title = 'switch문의 case/default에서 선언문은 중괄호로 감싼다.';

const rule = 'no-case-declarations';

const desc = null;

const options = 'error';

const cause = null;

const lintCategory = LINT_CATEGORY.BEST_PRACTICES;

const airbnbCategory = AIRBNB_CATEGORY.BLOCKS;

/* eslint-disable no-unused-vars, no-undef, no-unused-expressions, no-redeclare, no-inner-declarations, no-magic-numbers */
/* eslint-disable no-case-declarations */
const bad = () => {
  switch (foo) {
    case 1:
      const x = 1;
      break;
    case 2:
      const y = 2;
      break;
    case 3:
      function f () {}
      break;
    default:
      class C {}
  }
};
