// for typescript-parse check
interface MyInf {
  name: string;
  age: number;
}

interface TempType {
  a: any;
  b: any;
  c?: any;
}

const check = (person: MyInf): TempType => {
  const obj = {
    a: person.age,
    b: person.name,
  };

  return obj;
};

console.log(check({name: 'name', age: 1}));
