/* eslint-disable @typescript-eslint/member-delimiter-style */
// 구분자가 생략됨
interface Foo {
  name: string
  greet(): string
}

// 구분자로 세미콜론을 사용하지 않음
interface Bar {
  name: string,
  greet(): string,
}

// 마지막 멤버에 세미콜론이 생략됨
interface Baz {
  name: string;
  greet(): string
}

// 한 줄 멤버에 구분자를 사용함
interface Foo { name: string; }
/* eslint-enable @typescript-eslint/member-delimiter-style */
